<template>
  <div>
    <van-nav-bar title="小智同学" left-arrow @click-left="$router.back()" />

    <!--  -->
    <ul class="content" v-cloak>
      <!-- 循环遍历渲染 : 方式一-->
      <li v-for="item in list">
        <img
          :src="item.isme ? 'data:image/jpeg;base64,' : 
        'data:image/jpeg;base64,'"
          :class="item.isme ? 'imgright' : 'imgleft'"
        />
        <span :class="item.isme ? 'spanright' : 'spanleft'">{{item.msg}}</span>
      </li>
    </ul>
    <!--  -->
    <van-cell class="bottom-comment">
      <div class="comment-box">
        <van-cell-group class="inpt">
          <van-field
            v-model="msg"
            placeholder="说点什么"
            style="background-color: #eee; border-radius: 20px;"
            left-icon="edit"
          />
        </van-cell-group>
        <van-button type="danger" @click="send">发送</van-button>
      </div>
    </van-cell>
  </div>
</template>

<script>
import io from "socket.io-client";
export default {
  name: "xiaozhi",
  data() {
    return {
      msg: "",
      socket: null,
      list: []
    };
  },
  created() {
    this.socket = io("http://ttapi.research.itcast.cn", {
      query: {
        a: "sb"
      }
    });

    // 2.接收服务端数据时
    this.socket.on("message", data => {
      // console.log(data);
      this.list.push(data);
    });
  },
  methods: {
    // 3.在一个事件触发的时候,通过emit发消息;
    send() {
      let Obj = {
        msg: this.msg,
        timestamp: new Date().getTime()
      };
      this.socket.emit("message", Obj);
      this.list.push(Obj);
      Obj.isme = true;
      // console.log(this.list);
      this.msg = "";
    }
  }
};
</script>

